// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/utils/cryptography/EIP712.sol";

/**
 * @title TambrForwarder
 * @dev MinimalForwarder implementation for EIP-2771 meta-transactions.
 * This contract will be the VERIFYING_CONTRACT for the Relayer service.
 */
contract TambrForwarder is EIP712 {
    using ECDSA for bytes32;

    struct ForwardRequest {
        address from;
        address to;
        uint256 value;
        uint256 gas;
        uint256 nonce;
        bytes data;
    }

    bytes32 private constant TYPEHASH =
        keccak256("ForwardRequest(address from,address to,uint256 value,uint256 gas,uint256 nonce,bytes data)");

    mapping(address => uint256) private nonces;

    event MetaTransactionExecuted(address indexed from, address indexed to, bytes returnData);

    constructor() EIP712("TambrForwarder", "1") {}

    function getNonce(address from) public view returns (uint256) {
        return nonces[from];
    }

    function verify(ForwardRequest calldata req, bytes calldata signature) public view returns (bool) {
        address signer = _hashTypedDataV4(
            keccak256(
                abi.encode(
                    TYPEHASH,
                    req.from,
                    req.to,
                    req.value,
                    req.gas,
                    req.nonce,
                    keccak256(req.data)
                )
            )
        ).recover(signature);
        return signer == req.from;
    }

    function execute(ForwardRequest calldata req, bytes calldata signature) public payable returns (bool, bytes memory) {
        require(verify(req, signature), "Signature verification failed");
        require(nonces[req.from]++ == req.nonce, "Nonce mismatch");

        (bool success, bytes memory returnData) = req.to.call{gas: req.gas, value: req.value}(req.data);

        emit MetaTransactionExecuted(req.from, req.to, returnData);

        return (success, returnData);
    }
}

src/contracts/GovernanceToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title GovernanceToken
 * @dev ERC-20 governance token for Tambr protocol
 * 10% of total supply allocated to founder at deployment
 */
contract GovernanceToken is ERC20, Ownable {
    address public founderAddress;
    uint256 public constant FOUNDER_ALLOCATION_PERCENTAGE = 10; // 10%
    uint256 public constant TOTAL_SUPPLY = 1_000_000_000 * 10**18; // 1 billion tokens

    event FounderAllocationSet(address indexed founder, uint256 amount);

    constructor(address _founderAddress) ERC20("Tambr Governance Token", "TAMBR") Ownable(msg.sender) {
        require(_founderAddress != address(0), "Invalid founder address");

        founderAddress = _founderAddress;

        // Mint total supply
        _mint(address(this), TOTAL_SUPPLY);

        // Allocate 10% to founder
        uint256 founderAllocation = (TOTAL_SUPPLY * FOUNDER_ALLOCATION_PERCENTAGE) / 100;
        require(transfer(founderAddress, founderAllocation), "Founder allocation failed");

        emit FounderAllocationSet(founderAddress, founderAllocation);
    }

    /**
     * @dev Get founder's allocation percentage
     */
    function getFounderAllocationPercentage() public pure returns (uint256) {
        return FOUNDER_ALLOCATION_PERCENTAGE;
    }

    /**
     * @dev Get founder's current balance
     */
    function getFounderBalance() public view returns (uint256) {
        return balanceOf(founderAddress);
    }
}

src/contracts/IRRStablecoin.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Pausable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/metatx/ERC2771Context.sol";

/**
 * @title IRRStablecoin
 * @dev ERC-20 stablecoin backed by Iranian Rial (IRR)
 * Supports minting and burning by authorized entities
 */
contract IRRStablecoin is ERC20, ERC20Pausable, AccessControl, ERC2771Context {
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant BURNER_ROLE = keccak256("BURNER_ROLE");
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");

    event MinterAdded(address indexed account);
    event MinterRemoved(address indexed account);
    event BurnerAdded(address indexed account);
    event BurnerRemoved(address indexed account);

    constructor(address _trustedForwarder) ERC20("IRR Stablecoin", "IRR") ERC2771Context(_trustedForwarder) {
        _grantRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _grantRole(MINTER_ROLE, _msgSender());
        _grantRole(BURNER_ROLE, _msgSender());
        _grantRole(PAUSER_ROLE, _msgSender());
    }

    /**
     * @dev Mint new IRR tokens
     * @param to Recipient address
     * @param amount Amount to mint
     */
    function mint(address to, uint256 amount) public onlyRole(MINTER_ROLE) {
        _mint(to, amount);
    }

    /**
     * @dev Burn IRR tokens
     * @param from Address to burn from
     * @param amount Amount to burn
     */
    function burn(address from, uint256 amount) public onlyRole(BURNER_ROLE) {
        _burn(from, amount);
    }

    /**
     * @dev Pause token transfers
     */
    function pause() public onlyRole(PAUSER_ROLE) {
        _pause();
    }

    /**
     * @dev Unpause token transfers
     */
    function unpause() public onlyRole(PAUSER_ROLE) {
        _unpause();
    }

    /**
     * @dev Add a minter role to an account
     */
    function addMinter(address account) public onlyRole(DEFAULT_ADMIN_ROLE) {

        _grantRole(MINTER_ROLE, account);
        emit MinterAdded(account);
    }

    /**
     * @dev Remove minter role from an account
     */
    function removeMinter(address account) public onlyRole(DEFAULT_ADMIN_ROLE) {

        _revokeRole(MINTER_ROLE, account);
        emit MinterRemoved(account);
    }

    /**
     * @dev Add a burner role to an account
     */
    function addBurner(address account) public onlyRole(DEFAULT_ADMIN_ROLE) {

        _grantRole(BURNER_ROLE, account);
        emit BurnerAdded(account);
    }

    /**
     * @dev Remove burner role from an account
     */
    function removeBurner(address account) public onlyRole(DEFAULT_ADMIN_ROLE) {

        _revokeRole(BURNER_ROLE, account);
        emit BurnerRemoved(account);
    }

    // Required overrides
    function _msgSender() internal view virtual override(Context, ERC2771Context) returns (address) {
        return ERC2771Context._msgSender();
    }

    function _msgData() internal view virtual override(Context, ERC2771Context) returns (bytes calldata) {
        return ERC2771Context._msgData();
    }

    function _contextSuffixLength() internal view virtual override(Context, ERC2771Context) returns (uint256) {
        return ERC2771Context._contextSuffixLength();
    }

    function _update(
        address from,
        address to,
        uint256 amount
    ) internal override(ERC20, ERC20Pausable) {
        super._update(from, to, amount);
    }
}

src/contracts/MultiSigWallet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title MultiSigWallet
 * @dev A simple Multi-signature wallet contract for managing the Treasury.
 * Requires a minimum number of confirmations to execute a transaction.
 */
contract MultiSigWallet {
    event Deposit(address indexed sender, uint256 value);
    event Submission(uint256 indexed transactionId);
    event Confirmation(address indexed owner, uint256 indexed transactionId);
    event Revocation(address indexed owner, uint256 indexed transactionId);
    event Execution(uint256 indexed transactionId);

    mapping(address => bool) public isOwner;
    address[] public owners;
    uint256 public required;

    struct Transaction {
        address destination;
        uint256 value;
        bytes data;
        bool executed;
    }

    Transaction[] public transactions;

    mapping(uint256 => mapping(address => bool)) public confirmations;

    modifier onlyOwner() {
        require(isOwner[msg.sender], "Not owner");
        _;
    }

    modifier notExecuted(uint256 _transactionId) {
        require(!transactions[_transactionId].executed, "Tx already executed");
        _;
    }

    modifier notConfirmed(uint256 _transactionId) {
        require(!confirmations[_transactionId][msg.sender], "Tx already confirmed");
        _;
    }

    constructor(address[] memory _owners, uint256 _required) payable {
        require(_owners.length > 0, "Owners required");
        require(_required > 0 && _required <= _owners.length, "Invalid required number");

        for (uint256 i = 0; i < _owners.length; i++) {
            address owner = _owners[i];
            require(owner != address(0), "Invalid owner address");
            require(!isOwner[owner], "Owner not unique");
            isOwner[owner] = true;
            owners.push(owner);
        }
        required = _required;
    }

    receive() external payable {
        emit Deposit(msg.sender, msg.value);
    }

    /**
     * @dev Allows an owner to submit a new transaction.
     * @param _destination The address of the transaction destination.
     * @param _value The amount of Ether to send.
     * @param _data The data to send along with the transaction.
     * @return transactionId The ID of the submitted transaction.
     */
    function submitTransaction(
        address _destination,
        uint256 _value,
        bytes memory _data
    ) public onlyOwner returns (uint256 transactionId) {
        transactionId = transactions.length;
        transactions.push(
            Transaction({
                destination: _destination,
                value: _value,
                data: _data,
                executed: false
            })
        );
        emit Submission(transactionId);
        confirmTransaction(transactionId);
    }

    /**
     * @dev Allows an owner to confirm a transaction.
     * @param _transactionId The ID of the transaction to confirm.
     */
    function confirmTransaction(uint256 _transactionId)
        public
        onlyOwner
        notExecuted(_transactionId)
        notConfirmed(_transactionId)
    {
        confirmations[_transactionId][msg.sender] = true;
        emit Confirmation(msg.sender, _transactionId);

        if (getConfirmationCount(_transactionId) >= required) {
            executeTransaction(_transactionId);
        }
    }

    /**
     * @dev Executes a transaction if it has enough confirmations.
     * @param _transactionId The ID of the transaction to execute.
     */
    function executeTransaction(uint256 _transactionId) public onlyOwner notExecuted(_transactionId) {
        require(getConfirmationCount(_transactionId) >= required, "Not enough confirmations");

        Transaction storage txn = transactions[_transactionId];
        txn.executed = true;

        (bool success, ) = txn.destination.call{value: txn.value}(txn.data);
        require(success, "Tx execution failed");

        emit Execution(_transactionId);
    }

    /**
     * @dev Returns the number of confirmations for a transaction.
     * @param _transactionId The ID of the transaction.
     * @return count The number of confirmations.
     */
    function getConfirmationCount(uint256 _transactionId) public view returns (uint256 count) {
        for (uint256 i = 0; i < owners.length; i++) {
            if (confirmations[_transactionId][owners[i]]) {
                count += 1;
            }
        }
    }
}

src/contracts/SmartTicket.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Royalty.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title SmartTicket
 * @dev ERC-721 NFT with royalty support (EIP-2981)
 * Used for event tickets, digital assets, and collectibles
 */
contract SmartTicket is ERC721, ERC721Enumerable, ERC721Royalty, Ownable {
    uint256 private _nextTokenId = 1;
    mapping(uint256 => bool) private _redeemed;

    string public baseURI;
    address public royaltyReceiver;
    uint96 public royaltyPercentage; // In basis points (e.g., 500 = 5%)

    event TicketMinted(
        uint256 indexed tokenId,
        address indexed to,
        string uri
    );

    event RoyaltyUpdated(
        address indexed receiver,
        uint96 percentage
    );

    constructor(
        string memory name,
        string memory symbol,
        string memory _baseURI,
        address _minter,
        address _royaltyReceiver,
        uint96 _royaltyPercentage
    ) ERC721(name, symbol) Ownable(msg.sender) {
        // The minter/creator of the event is the owner, but we'll use a separate role for redemption
        _transferOwnership(_minter);
        baseURI = _baseURI;
        royaltyReceiver = _royaltyReceiver;
        royaltyPercentage = _royaltyPercentage;

        // Set default royalty
        _setDefaultRoyalty(_royaltyReceiver, _royaltyPercentage);
    }

    /**
     * @dev Mint a new ticket
     */
    function mint(address to) public onlyOwner returns (uint256) {
        uint256 tokenId = _nextTokenId++;
        _safeMint(to, tokenId);

        emit TicketMinted(tokenId, to, "");

        return tokenId;
    }

    /**
     * @dev Mint multiple tickets
     */
    function mintBatch(address to, uint256 quantity) public onlyOwner returns (uint256[] memory) {
        uint256[] memory tokenIds = new uint256[](quantity);

        for (uint256 i = 0; i < quantity; i++) {
            uint256 tokenId = _nextTokenId++;
            _safeMint(to, tokenId);
            tokenIds[i] = tokenId;

            emit TicketMinted(tokenId, to, "");
        }

        return tokenIds;
    }

    /**
     * @dev Update royalty information
     */
    function setRoyalty(address receiver, uint96 percentage) public onlyOwner {
        require(receiver != address(0), "Invalid receiver");
        require(percentage <= 10000, "Percentage too high"); // Max 100%

        royaltyReceiver = receiver;
        royaltyPercentage = percentage;

        _setDefaultRoyalty(receiver, percentage);

        emit RoyaltyUpdated(receiver, percentage);
    }

    /**
     * @dev Get next token ID
     */
    function getNextTokenId() public view returns (uint256) {
        return _nextTokenId;
    }

    /**
     * @dev Set base URI
     */
    function setBaseURI(string memory _baseURI) public onlyOwner {
        baseURI = _baseURI;
    }

    /**
     * @dev Get token URI
     */
    function tokenURI(uint256 tokenId)
        public
        view
        override
        returns (string memory)
    {
        _requireOwned(tokenId);

        if (bytes(baseURI).length == 0) {
            return "";
        }

        return string(abi.encodePacked(baseURI, _toString(tokenId), ".json"));
    }

    // Required overrides
    function _update(address to, uint256 tokenId, address auth)
        internal
        override(ERC721, ERC721Enumerable)
        returns (address)
    {
        return super._update(to, tokenId, auth);
    }

    function _increaseBalance(address account, uint128 value)
        internal
        override(ERC721, ERC721Enumerable)
    {
        super._increaseBalance(account, value);
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC721, ERC721Enumerable, ERC721Royalty)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    /**
     * @dev Marks a ticket as redeemed (used). Only callable by the contract owner (Event Creator/Platform).
     * The ticket must not have been redeemed already.
     */
    function redeemTicket(uint256 tokenId) public onlyOwner {
        // ownerOf reverts if token does not exist
        ownerOf(tokenId);
        require(!_redeemed[tokenId], "SmartTicket: token already redeemed");

        _redeemed[tokenId] = true;
        emit TicketRedeemed(tokenId, msg.sender);
    }

    /**
     * @dev Checks if a ticket has been redeemed.
     */
    function isRedeemed(uint256 tokenId) public view returns (bool) {
        return _redeemed[tokenId];
    }

    event TicketRedeemed(uint256 indexed tokenId, address indexed redeemer);

    // Helper function to convert uint to string
    function _toString(uint256 value) internal pure returns (string memory) {
        if (value == 0) {
            return "0";
        }
        uint256 temp = value;
        uint256 digits;
        while (temp != 0) {
            digits++;
            temp /= 10;
        }
        bytes memory buffer = new bytes(digits);
        while (value != 0) {
            digits -= 1;
            buffer[digits] = bytes1(uint8(48 + uint256(value % 10)));
            value /= 10;
        }
        return string(buffer);
    }
}

