// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";


/**
 * @title TambrDynamicBondingCurve
 * @dev Implements the Dynamic Bonding Curve logic for the IRRStablecoin.
 * The curve is based on a simple linear model for demonstration, with a founder fee mechanism.
 * The actual complex mathematical logic for the DBC needs to be fully implemented.
 */
contract TambrDynamicBondingCurve is Ownable {
    IERC20 public immutable stablecoin;
    address public treasury;
    uint256 public founderFeeRate; // e.g., 500 for 5% (500 / 10000)
    uint256 public constant FEE_DENOMINATOR = 10000;

    // Total supply of stablecoin in the curve's reserve
    uint256 public reserveBalance;

    event Buy(address indexed buyer, uint256 amountIn, uint256 tokensOut, uint256 founderFee);
    event Sell(address indexed seller, uint256 tokensIn, uint256 amountOut, uint256 founderFee);
    event TreasuryUpdated(address newTreasury);
    event FounderFeeRateUpdated(uint256 newRate);

    constructor(address _stablecoin, address _treasury, uint256 _founderFeeRate, address _trustedForwarder) Ownable(msg.sender) {
        stablecoin = IERC20(_stablecoin);
        treasury = _treasury;
        founderFeeRate = _founderFeeRate;
    }

    /**
     * @dev Sets the address of the treasury contract (Multi-sig).
     * @param _newTreasury The new treasury address.
     */
    function setTreasury(address _newTreasury) public onlyOwner {

        require(_newTreasury != address(0), "Zero address");
        treasury = _newTreasury;
        emit TreasuryUpdated(_newTreasury);
    }

    /**
     * @dev Sets the founder fee rate.
     * @param _newRate The new rate (e.g., 500 for 5%).
     */
    function setFounderFeeRate(uint256 _newRate) public onlyOwner {

        require(_newRate < FEE_DENOMINATOR, "Rate too high");
        founderFeeRate = _newRate;
        emit FounderFeeRateUpdated(_newRate);
    }

    /**
     * @dev Calculates the amount of stablecoin tokens received for a given amount of collateral (e.g., ETH/WETH).
     * @param _amountIn The amount of collateral being sent.
     * @return tokensOut The amount of stablecoin tokens to be minted.
     * @return feeAmount The founder fee amount.
     */
    function calculateBuyReturn(uint256 _amountIn) public view returns (uint256 tokensOut, uint256 feeAmount) {
        // TODO: Implement the full complex mathematical logic for the DBC (logarithmic/polynomial)
        // For now, we use a simple 1:1 ratio for demonstration.

        uint256 totalFee = (_amountIn * founderFeeRate) / FEE_DENOMINATOR;
        uint256 netAmount = _amountIn - totalFee;

        // Assuming 1:1 for simplicity
        tokensOut = netAmount; 
        feeAmount = totalFee;
    }

    /**
     * @dev Allows users to buy stablecoin tokens by sending collateral.
     * The collateral is assumed to be sent in the transaction (e.g., ETH or WETH via a wrapper).
     * For this example, we assume the collateral is the stablecoin itself for simplicity in a single-token curve.
     * In a real scenario, this would be a different token (e.g., WETH or a base token).
     * @param _amountIn The amount of collateral (e.g., WETH) to spend.
     * @param _minTokensOut The minimum amount of stablecoin tokens the buyer is willing to receive.
     */
    function buy(uint256 _amountIn, uint256 _minTokensOut) public {
        (uint256 tokensOut, uint256 feeAmount) = calculateBuyReturn(_amountIn);
        require(tokensOut >= _minTokensOut, "Slippage too high");

        // Transfer collateral to the contract (reserve)
        // In a real scenario, this would be a transfer of the collateral token
        // For this example, we assume the stablecoin is the collateral for simplicity
        require(stablecoin.transferFrom(msg.sender, address(this), _amountIn), "Collateral transfer failed");

        // Transfer founder fee to the treasury
        require(stablecoin.transfer(treasury, feeAmount), "Fee transfer failed");

        // Mint stablecoin tokens to the buyer
        // This requires the stablecoin contract to have a minting function callable by this contract
        // We assume the stablecoin contract has a mint function for now.
        // stablecoin.mint(msg.sender, tokensOut); 

        reserveBalance += (_amountIn - feeAmount);

        emit Buy(msg.sender, _amountIn, tokensOut, feeAmount);
    }

    /**
     * @dev Calculates the amount of collateral received for a given amount of stablecoin tokens.
     * @param _tokensIn The amount of stablecoin tokens being sold.
     * @return amountOut The amount of collateral (e.g., WETH) to be returned.
     * @return feeAmount The founder fee amount.
     */
    function calculateSellReturn(uint256 _tokensIn) public view returns (uint256 amountOut, uint256 feeAmount) {
        // TODO: Implement the full complex mathematical logic for the DBC (logarithmic/polynomial)
        // For now, we use a simple 1:1 ratio for demonstration.

        uint256 totalReturn = _tokensIn; // Assuming 1:1 for simplicity
        uint256 totalFee = (totalReturn * founderFeeRate) / FEE_DENOMINATOR;

        amountOut = totalReturn - totalFee;
        feeAmount = totalFee;
    }

    /**
     * @dev Allows users to sell stablecoin tokens for collateral.
     * @param _tokensIn The amount of stablecoin tokens to sell.
     * @param _minAmountOut The minimum amount of collateral the seller is willing to receive.
     */
    function sell(uint256 _tokensIn, uint256 _minAmountOut) public {
        (uint256 amountOut, uint256 feeAmount) = calculateSellReturn(_tokensIn);
        require(amountOut >= _minAmountOut, "Slippage too high");

        // Burn stablecoin tokens from the seller
        // This requires the stablecoin contract to have a burn function callable by this contract
        // stablecoin.burn(msg.sender, _tokensIn);

        // Transfer founder fee to the treasury
        require(stablecoin.transfer(treasury, feeAmount), "Fee transfer failed");

        // Transfer collateral from the reserve to the seller
        // In a real scenario, this would be a transfer of the collateral token
        require(stablecoin.transfer(msg.sender, amountOut), "Collateral transfer failed");

        reserveBalance -= amountOut;

        emit Sell(msg.sender, _tokensIn, amountOut, feeAmount);
    }
}

src/contracts/TicketMarketplace.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "../interfaces/IERC2981.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";


/**
 * @title TicketMarketplace
 * @dev Handles the secondary market trading of SmartTicket NFTs using IRRStablecoin.
 * It enforces platform fees and creator royalties.
 */
contract TicketMarketplace is Ownable {


    // --- State Variables ---
    IERC721 public immutable ticketContract;
IERC2981 public immutable royaltyContract;
    IERC20 public immutable stablecoinContract;
    address public immutable founderAddress;

    // Mapping from tokenId to its listing details
    struct Listing {
        address seller;
        uint256 price; // Price in stablecoin
        bool isListed;
    }
    mapping(uint256 => Listing) public listings;

    // Fees are in basis points (BPS). 10000 BPS = 100%
    uint256 public constant PLATFORM_FEE_BPS = 200; // 2.0% Platform Fee
    uint256 public constant FOUNDER_SHARE_BPS = 20; // 0.2% Founder's Share (0.1% of 2.0% is too small, using 0.2% of total sale)

    // --- Events ---
    event TicketListed(uint256 indexed tokenId, address indexed seller, uint256 price);
    event TicketBought(uint256 indexed tokenId, address indexed buyer, address indexed seller, uint256 price);
    event ListingCanceled(uint256 indexed tokenId, address indexed seller);
    event FeeDistributed(uint256 indexed tokenId, uint256 platformFee, uint256 founderShare, uint256 creatorRoyalty);

    // --- Constructor ---
    constructor(
        address _ticketContract,
        address _stablecoinContract,
        address _founderAddress
    ) Ownable(msg.sender) {
        require(_ticketContract != address(0), "Invalid ticket contract address");
        require(_stablecoinContract != address(0), "Invalid stablecoin contract address");
        require(_founderAddress != address(0), "Invalid founder address");

        ticketContract = IERC721(_ticketContract);
        royaltyContract = IERC2981(_ticketContract);
        stablecoinContract = IERC20(_stablecoinContract);
        founderAddress = _founderAddress;
    }

    // --- Core Functions ---

    /**
     * @dev Lists a ticket for sale on the marketplace.
     * The seller must have approved the marketplace to transfer the token.
     * @param tokenId The ID of the ticket to list.
     * @param price The sale price in stablecoin.
     */
    function listTicket(uint256 tokenId, uint256 price) external {
        require(price > 0, "Price must be greater than zero");
        require(ticketContract.ownerOf(tokenId) == msg.sender, "Not the owner of the ticket");
        require(ticketContract.getApproved(tokenId) == address(this), "Marketplace not approved for transfer");
        require(!listings[tokenId].isListed, "Ticket already listed");

        listings[tokenId] = Listing({
            seller: msg.sender,
            price: price,
            isListed: true
        });

        emit TicketListed(tokenId, msg.sender, price);
    }

    /**
     * @dev Buys a listed ticket.
     * The buyer must have approved the marketplace to spend the stablecoin.
     * @param tokenId The ID of the ticket to buy.
     */
    function buyTicket(uint256 tokenId) external {
        Listing storage listing = listings[tokenId];
        require(listing.isListed, "Ticket not listed");
        require(listing.seller != msg.sender, "Cannot buy your own ticket");

        uint256 price = listing.price;
        address seller = listing.seller;
        address buyer = msg.sender;

        // 1. Transfer stablecoin from buyer to marketplace
        require(stablecoinContract.transferFrom(buyer, address(this), price), "Stablecoin transfer failed");

        // 2. Calculate and distribute fees/royalties
        uint256 platformFee = (price * PLATFORM_FEE_BPS) / 10000;
        uint256 founderShare = (price * FOUNDER_SHARE_BPS) / 10000;

        // EIP-2981 Royalty Calculation (SmartTicket implements ERC2981)
        (address royaltyRecipient, uint256 royaltyAmount) = royaltyContract.royaltyInfo(tokenId, price);

        // Ensure total fees do not exceed price
        uint256 totalFees = platformFee + royaltyAmount;
        require(totalFees < price, "Total fees exceed price");

        // 3. Distribute fees
        // Founder's Share (part of the platform fee)
        require(stablecoinContract.transfer(founderAddress, founderShare), "Founder share transfer failed");

        // Creator Royalty
        if (royaltyAmount > 0 && royaltyRecipient != address(0)) {
            require(stablecoinContract.transfer(royaltyRecipient, royaltyAmount), "Royalty transfer failed");
        }

        // Platform's remaining fee (Owner of the Marketplace)
        uint256 remainingPlatformFee = platformFee - founderShare;
        require(stablecoinContract.transfer(owner(), remainingPlatformFee), "Platform fee transfer failed");

        // 4. Transfer remaining amount to seller
        uint256 amountToSeller = price - totalFees;
        require(stablecoinContract.transfer(seller, amountToSeller), "Seller payment failed");

        // 5. Transfer NFT from seller to buyer
        ticketContract.safeTransferFrom(seller, buyer, tokenId);

        // 6. Clean up listing
        delete listings[tokenId];

        emit TicketBought(tokenId, buyer, seller, price);
        emit FeeDistributed(tokenId, platformFee, founderShare, royaltyAmount);
    }

    /**
     * @dev Cancels a ticket listing. Only callable by the seller.
     * @param tokenId The ID of the ticket to delist.
     */
    function cancelListing(uint256 tokenId) external {
        Listing storage listing = listings[tokenId];
        require(listing.isListed, "Ticket not listed");
        require(listing.seller == msg.sender, "Only the seller can cancel the listing");

        delete listings[tokenId];

        emit ListingCanceled(tokenId, msg.sender);
    }

    // --- Owner Functions ---

    /**
     * @dev Withdraws stablecoin from the contract. Only callable by the owner.
     * This is for withdrawing the platform's share of the fees (which is sent to the owner's address in buyTicket).
     */
    function withdrawStablecoin(uint256 amount) external onlyOwner {
        require(stablecoinContract.transfer(msg.sender, amount), "Withdrawal failed");
    }
}

